#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="$basedir\C:\Users\zxc66\Documents\Workspace\demo\实现一个前端脚手架\generator-niconico\node_modules\.store\shelljs@0.8.5\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="$basedir/C:/Users/zxc66/Documents/Workspace/demo/实现一个前端脚手架/generator-niconico/node_modules/.store/shelljs@0.8.5/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$env_node_path$pathsep$new_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../../../../shelljs@0.8.5/node_modules/shelljs/bin/shjs" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../../../../shelljs@0.8.5/node_modules/shelljs/bin/shjs" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../../../../shelljs@0.8.5/node_modules/shelljs/bin/shjs" $args
  } else {
    & "node$exe"  "$basedir/../../../../../shelljs@0.8.5/node_modules/shelljs/bin/shjs" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
